import "./App.css";
import Nav from "./components/Nav/Nav.jsx";
import Cards from "./components/Cards/Cards.jsx";
import React, {useState} from "react";
import axios from "axios";


function App() {
  const onClose = (id) => window.alert("Emulamos que se cierra la card " + id);

  const [characters, setCharacters]= useState ([]);

  const searchCharacters= (id)=> { 
    axios(`https://rym2.up.railway.app/api/character/${id}?key={pi-matijvillagran}`).then(
      ({ data }) => {
         if (data.name) {
            setCharacters((oldChars) => [...oldChars, data]);
         } else {
            window.alert('¡No hay personajes con este ID!');
         }
      }
   );
  }

  return (
    <div className="App">
      <Nav searchCharacters={searchCharacters} />
      <Cards characters={characters} onClose={onClose} />

    </div>
  );
}

export default App;
