import { ADD_FAV, FILTER, ORDER, REMOVE_FAV } from "../Action/Action";

const initialState = {
  myFavorites: [],
  allCharacters: [],
  filteredCharacters: [],
};

const rootReducer = (state = initialState, action) => {
  const { type, payload } = action; //destructuring del parametro action

  switch (type) {
    case ADD_FAV:
      return { ...state, myFavorites: payload };

    case REMOVE_FAV:
      return { ...state, myFavorites: payload };

    
    case FILTER:

        return{
            ...state,
            filteredCharacters: payload !== 'all' ? state.allCharacters.filter((char)=>char.gender === payload) : state.allCharacters
      }

    case ORDER:
      const orderCharacters = [...state.myFavorites];
      if (payload === "A") {
        orderCharacters.sort((a, b) => a.id - b.id);
      } else {
        orderCharacters.sort((a, b) => b.id - a.id);
      }
      return { ...state, myFavorites: orderCharacters };

    default:
      return { ...state };
  }
};

export default rootReducer;
