import "./App.css";
import Nav from "./components/Nav/Nav.jsx";
import Cards from "./components/Cards/Cards.jsx";
import React, { useState } from "react";
import axios from "axios";

function App() {
  const [characters, setCharacters] = useState([]);

  const onClose = (id) => {
    const indent= parseInt (id, 10);
    setCharacters((oldChars) => oldChars.filter((el) => el.id !== indent));

  };

  const searchCharacters = (id) => {
    axios(
      `https://rym2.up.railway.app/api/character/${id}?key={pi-matijvillagran}`
    ).then(({ data }) => {
      if (data.name) {
        setCharacters((oldChars) => [...oldChars, data]);
      } else {
        window.alert("¡No hay personajes con este ID!");
      }
    });
  };

  return (
    <div className="App .fondo">
      <Nav searchCharacters={searchCharacters} />
      <Cards characters={characters} onClose={onClose} />
    </div>
  );
}

export default App;
