import { ADD_FAV, REMOVE_FAV } from "../Action/Action"


const initialState= {
    myFavorites:[],
    allCharacters:[]
}

const rootReducer= (state=initialState, action)=>{

    const {type, payload}= action; //destructuring del parametro action

    switch (type) {
        case ADD_FAV:
            return {...state, myFavorites: [...state.allCharacters, payload], allCharacters};

        case REMOVE_FAV:
            return {...state, myFavorites: state.myFavorites.filter((char)=> char.id !== payload)}    
           
        default:
            return ({...state})
    }
}

export default rootReducer;
