import { ADD_FAV, FILTER, ORDER, REMOVE_FAV } from "../Action/Action";

const initialState = {
  myFavorites: [],
  allCharacters: [],
  filteredCharacters: [],
};

const rootReducer = (state = initialState, action) => {
  const { type, payload } = action; //destructuring del parametro action

  switch (type) {
    case ADD_FAV:
      return { ...state, myFavorites: payload };

    case REMOVE_FAV:
      return { ...state, myFavorites: payload };

    
    case FILTER:

        return{
            ...state,
            filteredCharacters: payload !== 'all' ? state.allCharacters.filter((char)=>char.gender === payload) : state.allCharacters
      }

    case ORDER:
        const orderedCharacters = state.filteredCharacters.slice().sort((a, b)=>{
            //se ordena de la Asc
            if (payload === "A") {
                return a.id - b.id
            }else{
                return b.id - a.id
            }
        })
    return{
        ...state,filteredCharacters: orderedCharacters    
    }
    default:
      return { ...state };
  }
};

export default rootReducer;
