import { ADD_FAV, FILTER, ADD_CHARACTER, ORDER, REMOVE_FAV, REMOVED_CHARACTER } from "../Action/Action"


const initialState= {
    myFavorites:[],
    allCharacters:[],
    filteredCharacters:[]
}

const rootReducer= (state=initialState, action)=>{

    const {type, payload}= action; //destructuring del parametro action

    switch (type) {
        case ADD_FAV:
            return {...state, myFavorites: payload};

        case REMOVE_FAV:
            return {...state, myFavorites: payload}
            
        case FILTER:
            if (payload==="all"){
                return {...state, myFavorites:[...state.allCharacters]}
            }else{
            return {...state, myFavorites: state.allCharacters.filter((char)=>char.gender === payload)} }
            
        case ORDER:
            if (payload==="A"){
                return {...state, myFavorites: state.allCharacters.sort((a,b)=> a.id-b.id)}
            }else{
                if (payload==="D"){
                    return {...state, myFavorites: state.allCharacters.sort((a,b)=> b.id-a.id)}
                }
            }
                
           
        default:
            return ({...state})
    }
}

export default rootReducer;
